import React, { useEffect, useState, useContext } from 'react';
import api from '../api';
import { toast } from 'react-toastify';
import AuthContext from '../contexts/AuthContext';
import '../styles.css';

export default function QuestionList() {
  const [questions, setQuestions] = useState([]);
  const [search, setSearch] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('All');
  const [difficultyFilter, setDifficultyFilter] = useState('All');
  const { user } = useContext(AuthContext);

  const fetchQs = async () => {
    try {
      const { data } = await api.get('/questions');
      setQuestions(data);
    } catch (err) {
      toast.info('Could not load questions. Showing sample questions.');
      setQuestions([]);
    }
  };

  useEffect(() => { fetchQs(); }, []);

  const filteredQs = questions.filter(q =>
    (q.question_text.toLowerCase().includes(search.toLowerCase())) &&
    (categoryFilter === 'All' || q.category === categoryFilter) &&
    (difficultyFilter === 'All' || q.difficulty === difficultyFilter)
  );

  const vote = async (id) => {
    try {
      await api.post(`/questions/${id}/vote`);
      setQuestions(qs => qs.map(q => q.id === id ? { ...q, votes: q.votes+1 } : q));
    } catch (err) { toast.error('Vote failed'); }
  };

  const bookmark = async (id) => {
    try {
      await api.post('/users/bookmark', { questionId: id });
      toast.success('Bookmarked');
    } catch (err) { toast.error(err?.response?.data?.msg || 'Bookmark failed'); }
  };

  return (
    <div>
      <div className="filter-container">
        <input
          placeholder="Search..."
          value={search}
          onChange={e=>setSearch(e.target.value)}
        />
        <select value={categoryFilter} onChange={e=>setCategoryFilter(e.target.value)}>
          <option>All</option><option>DSA</option><option>Frontend</option><option>Backend</option>
        </select>
        <select value={difficultyFilter} onChange={e=>setDifficultyFilter(e.target.value)}>
          <option>All</option><option>Easy</option><option>Medium</option><option>Hard</option>
        </select>
      </div>

      {filteredQs.map(q => (
        <div key={q.id} className="question-card">
          <h4>{q.question_text}</h4>
          <div className="tags">
            <span className="category">{q.category}</span>
            <span className={`difficulty ${q.difficulty.toLowerCase()}`}>{q.difficulty}</span>
          </div>
          <div className="actions">
            <button onClick={()=>vote(q.id)}>Upvote ({q.votes})</button>
            {user && <button onClick={()=>bookmark(q.id)}>ðŸ’–</button>}
          </div>
        </div>
      ))}
    </div>
  );
}
