import React, { useEffect, useState, useContext } from 'react';
import api from '../api';
import { toast } from 'react-toastify';
import AuthContext from '../contexts/AuthContext';
import './styles.css';

export default function QuestionList() {
  const [questions, setQuestions] = useState([]);
  const [search, setSearch] = useState('');
  const { user } = useContext(AuthContext);
  const [showAnswerId, setShowAnswerId] = useState(null);

  // Fetch questions
  const fetchQuestions = async () => {
    try {
      const { data } = await api.get('/questions');
      setQuestions(data);
    } catch (err) {
      toast.info('Could not load questions. Showing sample questions.');
      setQuestions([
        { id: 1, question_text: 'What is React?', options: ['JS library', 'Framework', 'Language', 'Database'], correct_option: 'a', category: 'Frontend', difficulty: 'Easy', votes: 0 },
        { id: 2, question_text: 'What is Node.js?', options: ['JS runtime', 'Framework', 'Language', 'Database'], correct_option: 'a', category: 'Backend', difficulty: 'Medium', votes: 0 },
      ]);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  const filteredQuestions = questions.filter(q =>
    (q.question_text || '').toLowerCase().includes(search.toLowerCase())
  );

  // Vote function
  const vote = async (id) => {
    try {
      await api.post(`/questions/${id}/vote`);
      setQuestions(qs => qs.map(q => q.id === id ? { ...q, votes: q.votes + 1 } : q));
      toast.success('Vote added!');
    } catch (err) {
      console.error(err);
      toast.error(err?.response?.data?.msg || 'Vote failed');
    }
  };

  // Bookmark function
  const bookmark = async (id) => {
    if (!user) {
      toast.info('Login to bookmark questions.');
      return;
    }
    try {
      await api.post('/users/bookmark', { questionId: id });
      toast.success('Bookmarked!');
    } catch (err) {
      console.error(err);
      toast.error(err?.response?.data?.msg || 'Bookmark failed');
    }
  };

  return (
    <div className="question-list-container">
      <div className="search-container">
        <input
          type="text"
          placeholder="Search questions..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </div>

      {filteredQuestions.length === 0 && <p>No questions found.</p>}

      {filteredQuestions.map(q => (
        <div key={q.id} className="question-card">
          <h4 onClick={() => setShowAnswerId(showAnswerId === q.id ? null : q.id)} style={{ cursor: 'pointer' }}>
            {q.question_text}
          </h4>
          <p>Category: {q.category} • Difficulty: {q.difficulty}</p>
          <p>Votes: {q.votes}</p>

          <div className="question-actions">
            <button onClick={() => vote(q.id)}>Upvote</button>
            {user && <button onClick={() => bookmark(q.id)}>Bookmark</button>}
          </div>

          {showAnswerId === q.id && (
            <p className="answer">
              ✅ Answer: {q.correct_answer || 'N/A'}
            </p>
          )}
        </div>
      ))}
    </div>
  );
}
