import React, { useState } from 'react';
import './MockTest.css';

const questions = [
  { id:1, question_text:'What is React?', options:['JS library','Framework','Language','Database'], correct_option:'a' },
  { id:2, question_text:'What is Node.js?', options:['JS runtime','Framework','Language','Database'], correct_option:'a' },
];

export default function MockTest() {
  const [answers,setAnswers]=useState({});
  const [submitted,setSubmitted]=useState(false);

  const handleAnswer=(id, option)=>{ if(!submitted) setAnswers({...answers,[id]:option}); }
  const score=Object.keys(answers).reduce((acc,id)=>answers[id]===questions.find(q=>q.id==id).correct_option?acc+1:acc,0);

  return (
    <div className="mock-container">
      <h2>Mock Test</h2>
      {questions.map(q=>(
        <div key={q.id} className="mock-card">
          <h4>{q.question_text}</h4>
          {q.options.map((opt,idx)=>{
            const letter=String.fromCharCode(97+idx);
            const bg=submitted ? (q.correct_option===letter?'#d4edda':answers[q.id]===letter?'#f8d7da':'#fff') : answers[q.id]===letter?'#cce5ff':'#fff';
            return <button key={opt} style={{background:bg}} onClick={()=>handleAnswer(q.id,letter)} disabled={submitted}>{letter}) {opt}</button>
          })}
        </div>
      ))}
      {!submitted ? 
        <button onClick={()=>setSubmitted(true)}>Submit Test</button> :
        <p>Your score: {score}/{questions.length}</p>
      }
    </div>
  )
}
