import React, { useState, useContext } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import AuthContext from '../contexts/AuthContext';

export default function AddQuestion() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('DSA');
  const [difficulty, setDifficulty] = useState('Medium');
  const { token } = useContext(AuthContext); // get token
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      if (!token) throw new Error('You must be logged in');

      // Token is automatically sent via axios default headers
      await api.post('/questions', { title, description, category, difficulty });
      toast.success('Question added');
      nav('/');
    } catch (err) {
      console.error(err);
      toast.error(err?.response?.data?.msg || err.message || 'Add failed');
    }
  };

  return (
    <div className="card">
      <h3>Add Question</h3>
      <form onSubmit={submit}>
        <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} />
        <textarea placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} rows={6} />
        <input placeholder="Category" value={category} onChange={e => setCategory(e.target.value)} />
        <select value={difficulty} onChange={e => setDifficulty(e.target.value)}>
          <option>Easy</option>
          <option>Medium</option>
          <option>Hard</option>
        </select>
        <button type="submit">Add</button>
      </form>
    </div>
  );
}
