import React, { useState } from 'react';

export default function MockTest() {
  const questions = [
    {
      id: 1,
      title: 'What is React?',
      description: 'Explain React.',
      correct_answer: 'A JS library for UI',
      options: ['A JS library for UI', 'A database', 'A framework', 'A language'],
    },
    {
      id: 2,
      title: 'What is Node.js?',
      description: 'Explain Node.js.',
      correct_answer: 'A JS runtime',
      options: ['A JS runtime', 'CSS library', 'HTML framework', 'Database'],
    },
  ];

  const [selectedAnswers, setSelectedAnswers] = useState({});

  const handleAnswer = (qId, option) => {
    setSelectedAnswers(prev => ({ ...prev, [qId]: option }));
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', padding: '20px' }}>
      <h2>Mock Test</h2>

      {questions.map(q => (
        <div key={q.id} style={{ marginBottom: '20px', border: '1px solid #b45', padding: '15px', borderRadius: '8px' }}>
          <h4>{q.title}</h4>
          <p>{q.description}</p>

          {q.options.map((option, idx) => {
            const selected = selectedAnswers[q.id] === option;
            const isCorrect = option === q.correct_answer;
            const bgColor = selected ? (isCorrect ? '#d4edda' : '#f8d7da') : '#fff';
            const optionLetter = String.fromCharCode(97 + idx); // a, b, c, d

            return (
              <button
                key={option}
                onClick={() => handleAnswer(q.id, option)}
                disabled={!!selectedAnswers[q.id]}
                style={{
                  display: 'block',
                  margin: '5px 0',
                  padding: '10px',
                  width: '100%',
                  color:'black',
                  background: bgColor,
                  border: '1px solid #ccc',
                  cursor: selectedAnswers[q.id] ? 'not-allowed' : 'pointer',
                  textAlign: 'left',
                  borderRadius: '5px',
                }}
              >
                {optionLetter}) {option}
              </button>
            );
          })}

          {selectedAnswers[q.id] && (
            <p style={{ fontWeight: 'bold', marginTop: '10px' }}>
              {selectedAnswers[q.id] === q.correct_answer
                ? '✅ Correct!'
                : `❌ Wrong! Correct answer: ${q.correct_answer}`}
            </p>
          )}
        </div>
      ))}
    </div>
  );
}
