import React, { useEffect, useState, useContext } from 'react';
import api from '../api';
import { toast } from 'react-toastify';
import AuthContext from '../contexts/AuthContext';

export default function QuestionList() {
  const [questions, setQuestions] = useState([]);
  const [search, setSearch] = useState('');
  const { user } = useContext(AuthContext);
  const [showAnswerId, setShowAnswerId] = useState(null);

  const fetchQs = async () => {
    try {
      const { data } = await api.get('/questions');
      setQuestions(data); // set all questions first
    } catch (err) {
      toast.info('Could not load questions. Showing sample questions.');
      setQuestions([
        { id: 1, title: 'What is React?', description: 'Explain React.', correct_answer: 'React is a JS library for building UI.', category:'Frontend', difficulty:'Easy', votes:0 },
        { id: 2, title: 'What is Node.js?', description: 'Explain Node.js.', correct_answer: 'Node.js is a JS runtime built on Chrome V8.', category:'Backend', difficulty:'Medium', votes:0 },
        { id: 3, title: 'What is REST API?', description: 'Explain REST.', correct_answer: 'REST API follows REST principles for communication.', category:'Backend', difficulty:'Medium', votes:0 },
      ]);
    }
  };

  useEffect(() => {
    fetchQs();
  }, []);

  // Safe filtered questions based on search
  const filteredQuestions = questions.filter(q =>
    (q.title || '').toLowerCase().includes(search.toLowerCase()) ||
    (q.description || '').toLowerCase().includes(search.toLowerCase())
  );

  const vote = async (id) => {
    try {
      await api.post(`/questions/${id}/vote`);
      setQuestions(qs => qs.map(q => q.id === id ? { ...q, votes: q.votes + 1 } : q));
    } catch (err) {
      toast.error('Vote failed');
    }
  };

  const bookmark = async (id) => {
    try {
      await api.post('/users/bookmark', { questionId: id });
      toast.success('Bookmarked');
    } catch (err) {
      toast.error(err?.response?.data?.msg || 'Bookmark failed');
    }
  };

  return (
    <div>
      <div className="card">
        <input
          placeholder="Search questions..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          className="search-box"
        />
        <small>Search by title or description</small>
      </div>

      {filteredQuestions.length === 0 && <p>No questions found.</p>}

      {filteredQuestions.map(q => (
        <div className="card" key={q.id} style={{ marginBottom: '10px' }}>
          <h4
            style={{ cursor: 'pointer' }}
            onClick={() => setShowAnswerId(showAnswerId === q.id ? null : q.id)}
          >
            {q.title}
          </h4>
          <p>Category: {q.category} • Difficulty: {q.difficulty}</p>
          <p>Votes: {q.votes}</p>
          <button onClick={() => vote(q.id)}>Upvote</button>
          {user && <button onClick={() => bookmark(q.id)} style={{ marginLeft: 8 }}>Bookmark</button>}
          {showAnswerId === q.id && (
            <p style={{ background: '#e0ffe0', padding: '5px', marginTop: '5px' }}>
              ✅ Answer: {q.correct_answer}
            </p>
          )}
        </div>
      ))}
    </div>
  );
}
